mapper.update:
UPDATE reply SET
content = #{content} 
WHERE id = #{id}


UPDATE reply
SET `like` = (
    SELECT COUNT(*)
    FROM reply_likes
    WHERE reply_id = #{replyId}
)
WHERE id = #{replyId};

mapper.select:
SELECT EXISTS (
SELECT 1
FROM reply_likes
WHERE reply_id = #{replyId}
AND user_id = #{userId}
)
